(6966, 'PM_O_CC', 'MODULE_PAYMENT_OFFLINE_CC_MICROSECONDS', 'microseconds'),
(6967, 'PM_O_CC', 'MODULE_PAYMENT_OFFLINE_CC_LOG_MODULE_ID', 'Payment Module ID: '),
(6968, 'PM_O_CC', 'MODULE_PAYMENT_OFFLINE_CC_LOG_MODULE_NAME', 'Payment Module Name: '),
