(7056, 'PM_PP_CC', 'PAYMENT_STATUS_PENDING_VERIFY_MSG', 'The payment is pending because you, the merchant, are not yet Verified. You must verify your account before you can accept this payment.'),
(7057, 'PM_PP_CC', 'PAYMENT_STATUS_PENDING_OTHER_MSG', 'The payment is pending for an ''other reason''. For more information, contact customer service at <A HREF=''https://www.paypal.com/cgibin/webscr?cmd=_contactgeneral''>https://www.paypal.com/cgibin/webscr?cmd=_contactgeneral</A>.'),
